import java.math.BigInteger

/**
 * Find the factorial of a number.
 * Using Int/Long return type will not be enough as factorial values will increase exponentially.
 * Using BigInteger eliminates this problem
 */

/**
 * Solution 1: Regular Recursion (Easier to implement but slower and has edge cases)
 * This solution also has an edge case where after certain number(Ex: 10000) we will run into StackOverFlow Error
 */

fun findFactorialUsingRecursion(num: Int): BigInteger {
    return if (num == 1) {
        BigInteger.valueOf(1)
    } else {
        findFactorialUsingRecursion(num - 1) * BigInteger.valueOf(num.toLong())
    }
}

/**
 * Solution 2: Tail recursion (Memory and Speed Efficient)
 *  No need to keep track of multiple recursive calls on the call stack, as the current recursive call is the last one
 *  and there's no need to preserve the state of the current function call after making the recursive call
 */

tailrec fun findFactorialUsingTailRecursion(num: Int, accumulator: BigInteger = BigInteger.valueOf(1)): BigInteger {
    return if (num == 1) {
        accumulator
    } else {
        findFactorialUsingTailRecursion(num - 1, accumulator * BigInteger.valueOf(num.toLong()))
    }
}


fun main() {
    val number = (-100..20000).random()
    if (number < 1) {
        println("Factorial of a negative number is undefined")
    } else {
        println("Factorial of $number is ${findFactorialUsingTailRecursion(number)}")
    }
}

